MENU = {
    "espresso": {
        "ingredients": {
            "water": 50,
            "milk": 0,
            "coffee": 18,
        },
        "cost": 1.5,
    },
    "latte": {
        "ingredients": {
            "water": 200,
            "milk": 150,
            "coffee": 24,
        },
        "cost": 2.5,
    },
    "cappuccino": {
        "ingredients": {
            "water": 250,
            "milk": 100,
            "coffee": 24,
        },
        "cost": 3.0,
    }
}

resources = {
    "water": 300,
    "milk": 200,
    "coffee": 100,
}

# TODO: 1. Print report of all coffee machine resources


def remaining_sources(balance):
    water = resources["water"]
    milk = resources["milk"]
    coffee = resources["coffee"]
    account_total = balance
    print(f"Water: {water}ml")
    print(f"Milk: {milk}ml")
    print(f"Coffee: {coffee}g")
    print(f"Money: ${account_total}")


# TODO: 2. Check if the remaining resources is enough for a given coffee

def is_enough(coffee_type):
    water_need = MENU[coffee_type]["ingredients"]["water"]
    milk_need = MENU[coffee_type]["ingredients"]["milk"]
    coffee_need = MENU[coffee_type]["ingredients"]["coffee"]
    return resources["water"] >= water_need and resources["milk"] >= milk_need and resources["coffee"] >= coffee_need


# TODO: 3. Process coin, calculate the total


def count_coin(Quarters, Dimes, Nickles, Pennies):
    total = 0.25 * Quarters + 0.1 * Dimes + 0.05 * Nickles + 0.01 * Pennies
    return total

# Count the change


def count_change(money_have, coffee_type):
    money_need = MENU[coffee_type]["cost"]
    return round(money_have - money_need, 2)


# TODO: 4. Check if the transaction is successful

def transaction_successful(coins_total, coffee_type):
    money_need = MENU[coffee_type]["cost"]
    return coins_total >= money_need

